// 테마
export type ThemeType = 'primary' | 'secondary' | 'tertiary' | 'accent';

// 가로 채움 크기
export type FullType = 'full' | 'half';

// 크기
export type SizeType = 'large' | 'medium' | 'normal' | 'small' | 'mini';

// 간격
export type SpacingType = SizeType;

// 가로,세로 정렬
export type DirectionType = 'column' | 'row';

// 가로 정렬 타입
export type AlignType = 'left' | 'right' | 'center';
